// src/components/Services.js
import React from 'react';
import { Link, Routes, Route } from 'react-router-dom';
import './Services.css'; // Ensure you create this CSS file

const Services = () => {
  return (
    <div className="services-container">
      <h2>Our Services</h2>
      <p>At Music Hub, we offer a range of services to elevate your musical experience. Explore our offerings below:</p>
      <ul className="services-list">
        <li className="service-item">
          <Link to="design" className="service-link">Music Design</Link>
          <p className="service-description">Crafting unique soundscapes and compositions tailored to your needs.</p>
        </li>
        <li className="service-item">
          <Link to="development" className="service-link">Music Development</Link>
          <p className="service-description">Helping artists and producers develop their music and reach their full potential.</p>
        </li>
      </ul>

      <Routes>
        <Route path="design" element={<ServiceDetail title="Music Design" description="Our Music Design service focuses on creating unique soundscapes tailored to your projects. Whether you're looking for background music for videos, podcasts, or any other multimedia project, we can help!" />} />
        <Route path="development" element={<ServiceDetail title="Music Development" description="Our Music Development service aims to nurture and guide artists through the creative process. We provide mentorship, feedback, and resources to help you grow as a musician." />} />
      </Routes>
    </div>
  );
};

// Separate component for service details
const ServiceDetail = ({ title, description }) => {
  return (
    <div className="service-detail">
      <h3>{title}</h3>
      <p>{description}</p>
    </div>
  );
};

export default Services;
